import React, {ChangeEvent, useState, KeyboardEvent} from "react";
import {Button} from "./Button";
import {FilteredTasksType} from "./App";

type TodolistType = {
    title: string
    tasks: TasksType[]
    filteredTasks: (filterValue: FilteredTasksType) => void
    removeTask: (taskId: number) => void
    changeStatusTask: (taskId: number, isDone: boolean) => void
    addTask: (title: string) => void
}

export type TasksType = {
    id: number
    title: string
    isDone: boolean
}


export const Todolist = ({title, tasks, filteredTasks, removeTask, changeStatusTask, addTask}: TodolistType) => {

    const [newTask, setNewTask] = useState("")
    const [error, setError] = useState(false)

    const filteredTaskAll = (filterValue: FilteredTasksType) => {
        filteredTasks(filterValue)
    }
    const changeStatusTaskHandler = (taskId: number, e: ChangeEvent<HTMLInputElement>) => {
        changeStatusTask(taskId, e.currentTarget.checked)
    }
    const addTaskHandler = () => {
        if (newTask.trim()) {
            addTask(newTask.trim())
            setNewTask("")
        }
    }
    const onKeyInputHandler = (e: KeyboardEvent<HTMLInputElement>) => {
        if (e.key === "Enter") {
            addTask(newTask)
            setNewTask("")
        }
    }
    const onChangeInputHandler = (e: ChangeEvent<HTMLInputElement>) => {
        if (e.currentTarget) {
            setNewTask(e.currentTarget.value)
        }
    }


    return (
        <div>
            <h3>{title}</h3>
            <div>
                <input value={newTask} onChange={onChangeInputHandler} onKeyDown={onKeyInputHandler}/>
                <Button title={"+"} onClick={addTaskHandler}/>
            </div>
            <ul>
                {tasks.length
                    ? tasks.map(t => {
                            return (
                                <li key={t.id}>
                                    <input type="checkbox" checked={t.isDone}
                                           onChange={(e) => changeStatusTaskHandler(t.id, e)}/>
                                    <span>{t.title}</span>
                                    <Button onClick={() => removeTask(t.id)} title={"x"}/>
                                </li>
                            )
                        }
                    ) : <div>Todolist required</div>
                }
            </ul>
            <div>
                <Button onClick={() => filteredTaskAll("all")} title={"All"}/>
                <Button onClick={() => filteredTaskAll("active")} title={"Active"}/>
                <Button onClick={() => filteredTaskAll("completed")} title={"Completed"}/>
            </div>
        </div>
    )
}
