import {Button} from "./Button";
import React, {ChangeEvent, KeyboardEvent, useState} from "react";


type AddItemInputType = {
    title: string,
    addItem: (title: string) => void
}


export const AddItemInput = ({title, addItem}: AddItemInputType)=> {

    const [newTask, setNewTask] = useState("")
    const [error, setError] = useState(false)

    const addTaskHandler = () => {
        if (newTask.trim()) {
            addItem(title.trim())
            setNewTask("")
        } else {
            setError(true)
        }
    }
    const onKeyInputHandler = (e: KeyboardEvent<HTMLInputElement>) => {
        if (e.key === "Enter") {
            addItem(title.trim())
            setNewTask("")
        }
    }
    const onChangeInputHandler = (e: ChangeEvent<HTMLInputElement>) => {
        if (e.currentTarget) {
            setNewTask(e.currentTarget.value)
            setError(false)
        }
    }

    return (
        <div>
            <input value={newTask}
                   onChange={onChangeInputHandler}
                   onKeyDown={onKeyInputHandler}
                   className={error ? "error" : ""}
            />
            <Button title={"+"} onClick={addTaskHandler}/>
            {error && <h4 className={"errorMessage"}>input is empty</h4>}
        </div>
    )
}
